Custom Paint Job.command:
// This macro is designed to be used with Triggler from Combat Utility Belt. // Otherwise, it can be used as an alternative to manually using the Structure macro. // It also includes "destroying" the Custom Paint Job system in order to check if // it's been used between full repairs.
let cpjActor = game.actors.get(canvas.tokens.controlled[0].actor.id); //console.log(cpjActor);
async function scratchedThePaint(cpjId) {
    await cpjActor.update({"system.hp.value": 1});
    await cpjActor.updateEmbeddedDocuments("Item", [{_id: cpjId, 'system.destroyed': true}])
    return "Done.";
};
if (canvas.tokens.controlled.length !== 1) {
    ui.notifications.error("請選擇單一個目標");
    return;
}
let cpj = cpjActor.items.find(
    i => i.system.lid === "ms_custom_paint_job"
); if (cpj) {
  if (!cpj.system.destroyed) {
      console.log("Custom Paint Job found.");
      let roll = new Roll('1d6');
      await roll.roll();
    const html = await renderTemplate(
      `systems/${game.system.id}/templates/chat/generic-card.hbs`,
    {
      title: "個人塗裝",
      description: "",
      roll,
      roll_tt: await roll.getTooltip(),
    });
    const chatData = {
      type: CONST.CHAT_MESSAGE_TYPES.ROLL,
      roll: roll,
      speaker: {
      actor: cpjActor,
      token: cpjActor?.token,
      alias: !!cpjActor?.token ? cpjActor.token.name : null,
      },
      content: html,
    }
    await ChatMessage.create(chatData);
    console.log(`CPJ rolled ${roll.total}`);
    if (roll.total === 6) {
      await scratchedThePaint(cpj._id)
    } else {
      await cpjActor.beginStructureFlow();
    }
    return;
  } else {
    console.log("Custom Paint Job has already been used.");
  }
} // If we reach here, either the actor doesn't have CPJ or it's already been used. canvas.tokens.controlled[0].actor.beginStructureFlow();

Dismount/Eject.command:
if (!actor?.is_mech()) return;
const pilot = await fromUuid(actor.system.pilot?.id);
if (!pilot) return;

game.lancer.canvas.WeaponRangeTemplate.fromRange({
    type: "Blast",
    val: 0.1,
})
    .placeTemplate()
    .then(async t => {
        if (!t) return;
        const [x, y] = canvas.grid.getTopLeft(t.x, t.y);
        await TokenDocument.create(
            { ...pilot.prototypeToken, actorId: pilot.id, x, y },
            { parent: canvas.scene }
        );
        await t.delete();
    });

Mimic Gun.command:
// Calculate a Range for the Mimic Gun.
async function getRange() {
  const roll = await new Roll("d20").roll({ async: true });
  const text =
  `<i class="cci cci-range i--m"></i> ${roll.total}` +
  `&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;::&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;` +
  `${Math.ceil(roll.total / 2) + 1} ` +
  `<i class="cci cci-kinetic i--m damage--kinetic"></i>`;
  return text;
}

// Construct HTML message table for chat message.
async function buildMessageTable() {
  const messageTable =  
    "<b><h2>擬態槍射程</h2></b>" + 
    "<p><b>射程 X: </b>" + await getRange() +
    "<hr><p><b>射程 Y: </b>" + await getRange() +
    "<hr><p><b>射程 Z: </b>" + await getRange()
  return messageTable;
}

// Construct the chat message.
async function buildChatData() {
  return {
    user: game.user.id,
    speaker: ChatMessage.getSpeaker(),
    content: await buildMessageTable()
  };
}

ChatMessage.create(await buildChatData(), {});

Ruin Tokens.command:
function isBiological(token) {
  let actorItems = game.actors.filter(x => x.id === token.document.actorId)[0].items;
  for (let item of actorItems.entries()) {
    if (item[1].type === "npc_class" && item[1].system.role === "biological") {
      console.log(`${token.name} is biological.`);
      return true;
    } else {
      console.log(`${token.name} is not biological.`);
      return false;
    }
  }
}

if (canvas.tokens.controlled.length === 0) {
  ui.notifications.error("請選擇一個或更多的目標");
  return;
}
canvas.tokens.controlled.forEach(async t => {
  if (isBiological(t)) {
    ui.notifications.info(`${t.name}屬於生物，略過此操作`);
    return;
  }
  // CONFIG BELOW:
  // Data path where your images are stored.
  let IMAGE_PATH = "lancer-wrecks";

  // Dictionary for mapping sizes to subdirectories under IMAGE_PATH.
  // Keys are the potential sizes a LancerActor may have; values are the subdirectory names.
  // Store your images by size in each subdirectory.
  let SIZE_PATHS = {
    0.5: "S-HALF",
    1: "S1",
    2: "S2",
    3: "S3",
    4: "S4",
  };

  // Script:
  let size = t.actor.system.size;
  let sizePath = SIZE_PATHS[size];
  var imgString;
  if (!!sizePath) {
    var wreckFileList;
    try {
      let wreckFilePicker = await FilePicker.browse("data", `/${IMAGE_PATH}/${sizePath}`);
      wreckFileList = wreckFilePicker.files;
      console.log(wreckFileList);
      let numImages = wreckFileList.length;
      // Get random number between 1 and N, where n is number of images stored for mech size.
      let rand = Math.floor(Math.random() * numImages);
      imgString = wreckFileList[rand];
    } catch (error) {
      ui.notifications.warn(`${error}。略過圖片替換。`);
    }
  } else {
    ui.notifications.warn(
      `缺少${t.name}所需的大小${size}圖片路徑設定。略過圖片替換。`
    );
  }

  console.log(`Picked ${imgString} for ${t.name}`);

  let data = [
    {
      _id: t.id,
      "texture.src": imgString,
    },
  ];

  canvas.scene.updateEmbeddedDocuments("Token", data);
  t.actor.update({ "system.hp": size * 10, "system.heat": 0, "system.burn": 0 });
  game.combat.getCombatantByToken(t.id)?.delete();
  // t.update(data);
  // t.refresh();
});

Scan.command:
let targets = Array.from(game.user.targets);

//actorPermissionLevel - This sets the permission level for the parent actor of a token (from CONST.DOCUMENT_PERMISSION_LEVELS, use NONE, LIMITED, OBSERVER, or OWNER). If set to `null`, no change is made.
const actorPermissionLevel = null;

function sort_features(a, b) {
  return b.system.origin.base - a.system.origin.base;
}

function construct_features(items, origin) {
  let sc_list = ``;
  sc_list += `<p>${origin}</p>`;
  let sc_features = items.filter(f => f.system.origin.name === origin).sort(sort_features);
  sc_features.forEach(i => {
    let sc_name = ``;
    let sc_desc = ``;
    if (i.system.origin.name === "EXOTIC" && !i.system.origin.base) {
      sc_name = '<code class="horus--subtle">未知異形系統</code>';
      sc_desc = "???";
    } else {
      sc_name = i.name;
      if (i.system.effect) {
        sc_desc = i.system.effect;
      } else {
        sc_desc = "無項目描述";
      }
      if (i.system.trigger) {
        sc_desc = `觸發：${i.system.trigger}<br>${sc_desc}`;
      }
    }
    let sc_entry = `<details><summary>${sc_name}</summary><p>${sc_desc}</p></details>`;
    sc_list += sc_entry;
  });
  return sc_list;
}

function construct_templates(items) {
  let sc_templates = ``;
  if (!items || items.length == 0) {
    sc_templates += "<p>無</p>";
  } else {
    items.forEach(i => {
      sc_templates += `<p>${i.name}</p>`;
    });
  }

  sc_templates += "<br>";
  return sc_templates;
}

targets.forEach(target => {
  let actor = target.actor;
  if (actorPermissionLevel !== null) {
    const actorId = actor.system.parent._id;
    const parentActor = game.actors.get(actorId);
    parentActor.update({ ownership: { default: actorPermissionLevel } });
  }
  let items = actor.items;
  let hase_table_html = `
<table>
  <tr>
    <th>機體</th><th>敏捷</th><th>系統</th><th>工程</th>
  </tr>
  <tr>
    <td>${actor.system.hull || 0}</td>
    <td>${actor.system.agi || 0}</td>
    <td>${actor.system.sys || 0}</td>
    <td>${actor.system.eng || 0}</td>
  </tr>
</table>`;
  let stat_table_html = `
<table>
  <tr>
    <th>裝甲</th><th>HP</th><th>熱度</th><th>速度</th>
  </tr>
  <tr>
    <td>${actor.system.armor}</td>
    <td>${actor.system.hp.value}/${actor.system.hp.max}</td>
    <td>${actor.system.heat.value || 0}/${actor.system.heat.max || 0}</td>
    <td>${actor.system.speed}</td>
  </tr>
  <tr>
    <th>迴避</th><th>電子防禦</th><th>豁免目標</th><th>感應</th>
  </tr>
  <tr>
    <td>${actor.system.evasion}</td>
    <td>${actor.system.edef}</td>
    <td>${actor.system.save}</td>
    <td>${actor.system.sensor_range}</td>
  </tr>
  <tr>
    <th>大小</th><th>行動次數</th><th>結構</th><th>壓力</th>
  </tr>
  <tr>
    <td>${actor.system.size}</td>
    <td>${actor.system.activations || 1}</td>
    <td>${actor.system.structure.value || 0}/${actor.system.structure.max || 0}</td>
    <td>${actor.system.stress.value || 0}/${actor.system.stress.max || 0}</td>
  </tr>
</table>`;
  console.log("Scanning", target);
  const classes = items.filter(i => i.is_npc_class());
  let sc_class = !classes || classes.length === 0 ? "無" : classes[0].name;
  let sc_tier = actor.system.tier;
  const templates = items.filter(i => i.is_npc_template());
  let sc_templates = construct_templates(templates);
  let sc_list = ``;
  const features = items.filter(i => i.is_npc_feature());
  if (!features || features.length === 0) {
    sc_list += "<p>無</p>";
  } else {
    let sc_origins = [];
    features.forEach(f => {
      let origin = f.system.origin.name;
      if (!sc_origins.includes(origin)) {
        sc_origins.push(origin);
      }
    });
    sc_origins.forEach(origin => {
      sc_list += construct_features(features, origin);
    });
  }

  ChatMessage.create({
    user: game.user._id,
    content:
      `<h2>掃描結果：${actor.name}</h2>` +
      `<h3>種類：${sc_class}, 等級 ${sc_tier}</h3>` +
      hase_table_html +
      stat_table_html +
      `<h3>模板：</h3>` +
      sc_templates +
      `<h3>系統：</h3>` +
      sc_list,
    "flags.core.canPopout": true,
  });
});

Scan (Journal).command:
//Sanity Check - Can this user even create folders and journal entries? Does the folder journal folder exist?
if (JournalEntry.canUserCreate(game.user) == false) {
  ui.notifications.error(
    `${game.user.name} attempted to run SCAN to Journal but lacks proper permissions. Please correct and try again.`
  );
  return;
}

// ======================================================
//Variables - Change these to control the macro
// ======================================================

// outputToChat - if set to true, also send the scan results to chat.
const outputToChat = false;
//journalFolderName - The name, as displayed in foundry, of the folder you want the journal entries to save to. Remember to enclose in quotes: 'example'
const journalFolderName = "掃描資料庫";
//nameTemplate - The text before the scan number and target name. Remember to enclose in quotes: 'example'
const nameTemplate = "SCAN: ";
//numberLength - The total length of the scan number, extra spaces are filled with 0s. Setting this to 3, for example would produce scan number 001 on your first scan. Integers only and no quotes.
const numberLength = 3;
//startingNumber - If you want the scan number to start at something other than 1 then change this. Integers only and no quotes.
const startingNumber = 1;
//permissionLevel - This sets the default permission level of the scan entry (from CONST.DOCUMENT_OWNERSHIP_LEVELS, use NONE, LIMITED, OBSERVER, or OWNER).
const permissionLevel = CONST.DOCUMENT_OWNERSHIP_LEVELS.OWNER;
//updateExisting - This macro will check if a scan journal entry exists and update it, set this to false if you want it to create a new scan journal entry.
const updateExisting = true;
//actorPermissionLevel - This sets the permission level for the parent actor of a token (from CONST.DOCUMENT_PERMISSION_LEVELS, use NONE, LIMITED, OBSERVER, or OWNER). If set to `null`, no change is made.
const actorPermissionLevel = null;
//targets - Gets the data for your currently selected target(s) and stores it for later use. Do not change.
let targets = Array.from(game.user.targets);

// ===========================================================
// DO NOT MODIFY BELOW HERE UNLESS YOU KNOW WHAT YOU'RE DOING
// ===========================================================

// ======================================================
//Functions
// ======================================================

//zeroPad - Adds a set number 0s to the fed number to produce a consistent length number.
function zeroPad(num, places) {
  return String(num).padStart(places, "0");
}

//findItemByType - To find specific items in the .item array.
function findItemByType(array, itemType) {
  return array.filter(item => item.type === itemType);
}

//sort_features - Sorts the feature list for the scanned target
function sort_features(a, b) {
  return b.system.origin.base - a.system.origin.base;
}

//construct_features - Builds out the list of selectable features for the scanned target, includes support for exotics.
function construct_features(items, origin) {
  let sc_list = ``;
  sc_list += `<p>${origin}</p>`;
  let sc_features = items.filter(f => f.system.origin && f.system.origin.name === origin).sort(sort_features);
  sc_features.forEach(i => {
    let sc_name = ``;
    let sc_desc = ``;
    if (i.system.origin.name == "EXOTIC" && !i.system.origin.base) {
      sc_name = '<code class="horus--subtle">未知異形系統</code>';
      sc_desc = "???";
    } else {
      sc_name = i.name;
      if (i.system.effect) {
        sc_desc = i.system.effect;
      } else {
        sc_desc = "無項目描述";
      }
      if (i.system.trigger) {
        sc_desc = `觸發：${i.system.trigger}<br>${sc_desc}`;
      }
    }
    if (!sc_desc.startsWith("<p>") && !sc_desc.startsWith("<P>")) sc_desc = `<p>${sc_desc}</p>`;
    let sc_entry = `<details><summary>${sc_name}</summary>${sc_desc}</details>`;
    sc_list += sc_entry;
  });
  return sc_list;
}

function construct_weapons(items, origin, tier) {
  let sc_weapons = ``;
  let sc_features = items
    .filter(i => i.system.origin && i.system.origin.name === origin && i.system.type === "Weapon")
    .sort(sort_features);
  sc_features.forEach(i => {
    let sc_name = ``;
    let sc_desc = ``;
    let sc_entry = ``;
    let sc_range = ``;
    let sc_damage = ``;
    let sc_accuracy = ``;
    if (!i.type) {
      return sc_weapons;
    }
    sc_weapons += `<table>`;
    if (i.system.origin.name == "EXOTIC" && !i.system.origin.base) {
      sc_name = '<tr><th><code class="horus--subtle">未知異形武器</code></th></tr>';
      sc_desc = "<tr><td>???</td></tr>";
      sc_entry = sc_name + sc_desc;
    } else {
      sc_name = `<tr><th colspan="4">${i.name}</th></tr>`;
      sc_entry += sc_name;
      sc_desc = `<tr>`;
      sc_desc += `<td>+${i.system.attack_bonus[tier - 1]} 攻擊</td>`;
      if (i.system.accuracy[tier - 1]) {
        let acc = i.system.accuracy[tier - 1];
        sc_accuracy = `${acc > 0 ? "+" : ""}${acc} ${acc > 0 ? "準度" : "難度"}`;
      }
      sc_desc += `<td>${sc_accuracy}</td>`;
      if (i.system.range.length > 0) {
        i.system.range.forEach(r => (sc_range += r.type + " " + r.val + "&nbsp&nbsp&nbsp"));
      }
      sc_desc += `<td>${sc_range}</td>`;
      if (i.system.damage.length > 0) {
        i.system.damage[tier - 1].forEach(d => (sc_damage += d.val + " " + d.type + "&nbsp&nbsp&nbsp"));
      }
      sc_desc += `<td>${sc_damage}</td>`;
      if (i.system.tags.some(t => t.is_loading)) {
        if (i.system.loaded) {
          sc_desc += `<td>已裝填</td>`;
        } else {
          sc_desc += `<td>未裝填</td>`;
        }
      } else {
        sc_desc += `<td></td>`;
      }
      if (i.system.uses.max > 0) {
        sc_desc += `<td>USES: ${i.system.uses.value}/${i.system.uses.max}</td>`;
      }
      sc_desc += `<tr>`;
      if (i.system.trigger) {
        sc_desc += `<tr><td colspan="6"><details><summary>觸發</summary><p>${i.system.trigger}</p></details></td></tr>`;
      }
      if (i.system.on_hit) {
        sc_desc += `<tr><td colspan="6"><details><summary>命中時</summary><p>${i.system.on_hit}</p></details></td></tr>`;
      }
      if (i.system.effect) {
        sc_desc += `<tr><td colspan="6">${i.system.effect}</td></tr>`;
      }
      if (i.system.tags.length > 0) {
        sc_desc += `<tr><td colspan="6">標籤：`;
        sc_desc += i.system.tags.map(t => `${t.name.replace("{VAL}", t.val)}`).join(", ");
        sc_desc += `</td></tr>`;
      }
      sc_entry += sc_desc;
    }
    sc_weapons += sc_entry;
    sc_weapons += `</table>`;
  });
  return sc_weapons;
}

//construct_templates
function construct_templates(items) {
  let templatefind = findItemByType(items, "npc_template");
  let sc_templates = ``;
  let sc_temp = templatefind;
  if (!sc_temp || sc_temp.length == 0) {
    sc_templates += "<p>無</p>";
  } else {
    sc_temp.forEach(i => {
      let sc_entry = `<p>${i.name}</p>`;
      sc_templates += sc_entry;
    });
  }
  return sc_templates;
}

// Find or create the Scans folder
let journalFolder = game.folders.getName(journalFolderName);
if (!journalFolder && journalFolderName.length > 0) {
  try {
    journalFolder = await Folder.create({
      name: journalFolderName,
      type: "JournalEntry",
    });
  } catch (error) {
    ui.notifications.error(
      `${journalFolderName}不存在，必須由擁有權限的使用者手動建立。`
    );
    return;
  }
}

// Create the journal for each targeted token
targets.forEach(async target => {
  let actor = target.actor;
  if (actorPermissionLevel !== null) {
    const actorId = actor.system.parent._id;
    const parentActor = game.actors.get(actorId);
    parentActor.update({ ownership: { default: actorPermissionLevel } });
  }
  console.log("Scanning target:", actor);
  let items = actor.items;
  let hase_table_html = `
<p><img style="border: 3px dashed #000000; float: left; margin-right: 5px; margin-left: 5px;" src="${
    target.document.actor.img
  }" width="30%" height="30%" /></p>
<div style="color: #000000; width: 65%; float: right; text-align: left;">
<table>
  <tr>
    <th>機體</th><th>敏捷</th><th>系統</th><th>工程</th>
  </tr>
  <tr>
    <td>${actor.system.hull || 0}</td>
    <td>${actor.system.agi || 0}</td>
    <td>${actor.system.sys || 0}</td>
    <td>${actor.system.eng || 0}</td>
  </tr>
</table> `;
  let stat_table_html = `
<table>
  <tr>
    <th>裝甲</th><th>HP</th><th>熱度</th><th>速度</th>
  </tr>
  <tr>
    <td>${actor.system.armor}</td>
    <td>${actor.system.hp.value}/${actor.system.hp.max}</td>
    <td>${actor.system.heat.value || 0}/${actor.system.heat.max || 0}</td>
    <td>${actor.system.speed}</td>
  </tr>
  <tr>
    <th>迴避</th><th>電子防禦</th><th>豁免目標</th><th>感應</th>
  </tr>
  <tr>
    <td>${actor.system.evasion}</td>
    <td>${actor.system.edef}</td>
    <td>${actor.system.save}</td>
    <td>${actor.system.sensor_range}</td>
  </tr>
  <tr>
    <th>大小</th><th>行動次數</th><th>結構</th><th>壓力</th>
  </tr>
  <tr>
    <td>${actor.system.size}</td>
    <td>${actor.system.activations || 1}</td>
    <td>${actor.system.structure.value || 0}/${actor.system.structure.max || 0}</td>
    <td>${actor.system.stress.value || 0}/${actor.system.stress.max || 0}</td>
  </tr>
</table> `;
  console.log(items);
  let sc_class = !actor.system.class ? "無" : actor.system.class.name;
  let sc_tier = actor.system.tier ? actor.system.tier : 0;
  let sc_templates = construct_templates(items);
  let sc_list = ``;
  let sc_weapons = ``;
  if (!items || items.length == 0) {
    sc_list += "<p>無</p>";
    sc_weapons += "<p>無</p>";
  } else {
    let sc_origins = [];

    items.forEach(f => {
      let origin = f.system.origin?.name;
      if (origin && !sc_origins.includes(origin)) {
        sc_origins.push(origin);
      }
    });

    sc_origins.forEach(origin => {
      sc_list += construct_features(items, origin);
      sc_weapons += construct_weapons(items, origin, sc_tier);
    });
  }

  if (outputToChat) {
    ChatMessage.create({
      user: game.user._id,
      content: `<h2>掃描結果：${actor.name}</h2>
        <h3>種類：${sc_class}, 等級 ${sc_tier}</h3>
        ${hase_table_html}
        ${stat_table_html}
        <h3>模板：</h3>
        ${sc_templates}
        <h3>系統：</h3>
        ${sc_list}`,
    });
  }

  let scanContent =
    `<h2>掃描結果：${actor.name}</h2>` +
    `<h3>種類：${sc_class}, 等級 ${sc_tier}</h3>` +
    hase_table_html +
    stat_table_html +
    `</div><div style="color: #000000; width: 100%; float: right; text-align: left;"><h3>模板：</h3>` +
    sc_templates +
    `<h3>武器：</h3>` +
    sc_weapons +
    `<h3>系統：</h3>` +
    sc_list +
    `</div>`;

  //This checks and updates the scan entry for the target(s) if a single scan entry exists in the specified folder for the target(s) along with the updateExisting flag.
  //If either are false then this creates a new scan entry.

  let scanEntry;

  let matchingJournalEntries = journalFolder.contents.filter(e => e.name.includes(actor.name));

  if (matchingJournalEntries.length == 1 && updateExisting === true) {
    console.log("Updating an existing scan");
    const scanName = matchingJournalEntries[0].name;
    scanEntry = game.journal.getName(scanName);
    let scanPage = scanEntry.pages.getName(scanName);
    await scanPage.update({
      _id: matchingJournalEntries[0]._id,
      text: {
        content: scanContent,
      },
    });
  } else {
    console.log("Creating a new scan");
    let scanCount = zeroPad(
      journalFolder.contents.filter(e => e.name.startsWith(nameTemplate)).length + startingNumber,
      numberLength
    );
    const scanName = nameTemplate + scanCount + ` - ` + actor.name;
    let scanPage = new JournalEntryPage({
      name: scanName,
      type: "text",
      text: { content: scanContent },
    });
    scanEntry = await JournalEntry.create({
      folder: journalFolder.id,
      name: scanName,
    });
    scanEntry.createEmbeddedDocuments("JournalEntryPage", [scanPage]);
  }

  scanEntry.update({ permission: { default: permissionLevel } });
  scanEntry.sheet.render(true);
});

